BEGIN TRANSACTION;
CREATE TABLE `station` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` VARCHAR(255), `address` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `cityId` INTEGER REFERENCES `city` (`id`) ON DELETE SET NULL ON UPDATE CASCADE);
CREATE TABLE `start_time` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `time` TIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `company` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` VARCHAR(255), `phones` VARCHAR(255), `address` TEXT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `city` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `buslines` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` VARCHAR(255), `description` VARCHAR(255), `price` INTEGER, `paymethod` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `startCityId` INTEGER REFERENCES `city` (`id`) ON DELETE SET NULL ON UPDATE CASCADE, `endCityId` INTEGER REFERENCES `city` (`id`) ON DELETE SET NULL ON UPDATE CASCADE, `companyId` INTEGER REFERENCES `company` (`id`) ON DELETE SET NULL ON UPDATE CASCADE);
CREATE TABLE `busline_stations` (`freezeTableName` true, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `stationId` INTEGER NOT NULL REFERENCES `station` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, `buslineId` INTEGER NOT NULL REFERENCES `buslines` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, PRIMARY KEY (`stationId`, `buslineId`));
CREATE TABLE `busline_starttimes` (`freezeTableName` true, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `startTimeId` INTEGER NOT NULL REFERENCES `start_time` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, `buslineId` INTEGER NOT NULL REFERENCES `buslines` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, PRIMARY KEY (`startTimeId`, `buslineId`));
;
;
COMMIT;
